import React from "react";
import ReactDOM from "react-dom";
import "./index.css";

const heading = {
  color: "#fa9191",
  textAlign: "center",
  textTransform: "capitalize",
  fontWeight: "bold",
  textShadow: "0px 2px 4px #ffe9c5",
  margin: "50px 0px",
  fontFamily: "Lexend Zetta, sans-serif",
};

const img_div = {
  display: "flex",
  justifyContent: "center",
};
const cssStyle = {};

const name = "vinod";
const img1 = "https://picsum.photos/200/300";
const img2 = "https://picsum.photos/250/300";
const img3 = "https://picsum.photos/300/300";
const links = "https://www.google.com";

let cuurDate = new Date();
let greeting = "";
let hours = cuurDate.getHours();
if (hours >= 1 && hours < 12) {
  greeting = "Good morning";
  cssStyle.color = "green";
} else if (hours >= 12 && hours < 19) {
  greeting = "Good afternoon";
  cssStyle.color = "orange";
} else {
  greeting = "Good night";
  cssStyle.color = "black";
}
ReactDOM.render(
  <>
    <h1 className="heading" style={heading}>
      My name is {name}
    </h1>
    ,
    <div className="img_div" style={img_div}>
      <img src={img1} alt="randomImages"></img>
      <img src={img2} alt="randomImages"></img>
      <a href={links} target="_google">
        <img src={img3} alt="randomImages"></img>
      </a>
    </div>
    <h1>
      Hello sir, <span style={cssStyle}>{greeting}</span>
    </h1>
  </>,
  document.getElementById("root")
);
